import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../Style/SignUp.css";
import Menubar from "./Menubar";

function SignUp() {
  const navigate = useNavigate();

  const HandleClick = (url) => {
    navigate(url);
  };

  const state = {
    id_check: "",
    member_id: "",
    member_pass: "",
    gender: "",
    phone: "",
    nickname: "",
    SSN: "",
  };

  const Sign_Up = () => {
    alert(this.member_id);
  };

  const handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="App">
      <Menubar></Menubar>
      <div className="App-content-SignUp">
        <div>
          <textarea
            type="text"
            name="member_id"
            className="text"
            value={member_id}
            onChange={handleChange}
            placeholder="아이디"
          ></textarea>
        </div>
        &nbsp;
        <div>
          <textarea
            className="text"
            placeholder="비밀번호(8 ~ 32자)"
          ></textarea>
        </div>
        &nbsp;
        <div>
          <textarea className="text" placeholder="비밀번호 재입력"></textarea>
        </div>
        &nbsp;
        <div>
          <textarea className="text" placeholder="별명"></textarea>
        </div>
        &nbsp;
        <div>
          <textarea className="text" placeholder="이름"></textarea>
        </div>
        &nbsp;
        <div>
          <textarea className="text" placeholder="전화번호"></textarea>
        </div>
        &nbsp;
        <div>
          <input
            type="date"
            className="text"
            placeholder="생년월일 (YYYY/MM/DD)"
          />
        </div>
        &nbsp;
        <button onClick={Sign_Up} className="SignUp-button-SignUp">
          회원 가입
        </button>
      </div>
    </div>
  );
}

export default SignUp;
