// sign_up_function.js
const Return_Success = 0;
const Return_Duplicate_ID = 1;
const Return_error = 2;

async function Check_Sign_Up(formData, setFormData, navigate) {
  const scokets = require("../Component/moudle/sockets.js");
  var UserData_Save_Result = await scokets.UserData_Save(formData);

  alert(UserData_Save_Result);
  if (UserData_Save_Result === Return_Success) {
    // 회원 가입 성공 시 초기화
    setFormData({
      ID: "",
      PW: "",
      confirmPW: "",
      nickname: "",
      name: "",
      Phone: "",
      Birth: "",
    });
    alert("회원가입 완료");
    navigate("/Login");
  } else if (UserData_Save_Result === Return_Duplicate_ID) {
    alert("아이디 중복");
  } else {
    alert("오류");
  }
}

export async function Sign_Up(formData, setFormData, navigate) {
  // alert(JSON.stringify(formData));
  await Check_Sign_Up(formData, setFormData, navigate);
}
