import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../Style/SignUp.css";
import Menubar from "./Menubar";
import { AiFillEye, AiFillEyeInvisible } from "react-icons/ai";
import { Sign_Up } from "../Function/SignUp_Sign_up";
import { usePasswordToggle } from "../Function/SignUp_PasswordToggle";
import { useFormData } from "../Function/useFormData";
import { CheckUserInfo, checkFormData } from "./CheckUserInput.js";

function SignUp() {
  const navigate = useNavigate();
  const [errors, setErrors] = useState({
    ID: "",
    PW: "",
    confirmPW: "",
    nickname: "",
    name: "",
    Phone: "",
    Birth: "",
  });

  const [formData, handleChange, setFormData] = useFormData({
    ID: "",
    PW: "",
    confirmPW: "",
    nickname: "",
    name: "",
    Phone: "",
    Birth: "",
  });

  const {
    showPassword,
    toggleShowPassword,
    showConfirmPassword,
    toggleShowConfirmPassword,
  } = usePasswordToggle();

  const handleSignUp = () => {
    const errorMessages = {
      ID: "",
      PW: "",
      confirmPW: "",
      nickname: "",
      name: "",
      Phone: "",
      Birth: "",
    };

    var passwordRegex = /^(?=.*[a-zA-Z])(?=.*[0-9])(?=.*[!@#$%^&*?_]).{8,32}$/;
    var phoneRegex = /^\d{3}-\d{4}-\d{4}$/;

    const keysToCheck = [
      "ID",
      "PW",
      "confirmPW",
      "nickname",
      "name",
      "Phone",
      "Birth",
    ];

    if (!checkFormData(formData, keysToCheck)) {
      errorMessages.ID = "모든 필드를 입력해주세요.";
    }

    if (!phoneRegex.test(formData.Phone)) {
      errorMessages.Phone = "전화번호는 010-1234-5678 형식으로 입력해주세요.";
    }

    if (!passwordRegex.test(formData.PW)) {
      errorMessages.PW =
        "비밀번호는 8~32자 이내의 영문자, 숫자 및 특수 문자를 포함해야 합니다.";
    }

    if (formData.PW !== formData.confirmPW) {
      errorMessages.confirmPW = "비밀번호가 일치하지 않습니다.";
    }

    setErrors(errorMessages);

    if (Object.values(errorMessages).some((msg) => msg !== "")) {
      return;
    }

    // 회원 가입 성공 시 초기화
    setFormData({
      ID: "",
      PW: "",
      confirmPW: "",
      nickname: "",
      name: "",
      Phone: "",
      Birth: "",
    });

    alert("회원가입 완료");
    navigate("/Login");
  };

  return (
    <div className="App">
      <Menubar></Menubar>
      <div className="App-content-SignUp">
        <div>
          <input
            name="ID"
            className={`text ${errors.ID && "input-error"}`}
            value={formData.ID}
            onChange={handleChange}
            placeholder="아이디"
          ></input>
          {errors.ID && <div className="error-message">{errors.ID}</div>}
        </div>
        &nbsp;
        <div className="password-field">
          <input
            type={showPassword ? "text" : "password"}
            name="PW"
            className={`text password-input ${errors.PW && "input-error"}`}
            value={formData.PW}
            onChange={handleChange}
            placeholder="비밀번호(8 ~ 32자) 및 특수문자 포함"
          ></input>
          <span onClick={toggleShowPassword} className="password-icon">
            {showPassword ? <AiFillEye /> : <AiFillEyeInvisible />}
          </span>
          {errors.PW && <div className="error-message">{errors.PW}</div>}
        </div>
        &nbsp;
        <div className="password-field">
          <input
            type={showConfirmPassword ? "text" : "password"}
            name="confirmPW"
            className={`text ${errors.confirmPW && "input-error"}`}
            value={formData.confirmPW}
            onChange={handleChange}
            placeholder="비밀번호 재입력"
          ></input>
          <span onClick={toggleShowConfirmPassword} className="password-icon">
            {showConfirmPassword ? <AiFillEye /> : <AiFillEyeInvisible />}
          </span>
          {errors.confirmPW && (
            <div className="error-message">{errors.confirmPW}</div>
          )}
        </div>
        &nbsp;
        <div>
          <input
            name="nickname"
            className={`text ${errors.nickname && "input-error"}`}
            value={formData.nickname}
            onChange={handleChange}
            placeholder="별명"
          ></input>
          {errors.nickname && (
            <div className="error-message">{errors.nickname}</div>
          )}
        </div>
        &nbsp;
        <div>
          <input
            name="name"
            className={`text ${errors.name && "input-error"}`}
            value={formData.name}
            onChange={handleChange}
            placeholder="이름"
          ></input>
          {errors.name && <div className="error-message">{errors.name}</div>}
        </div>
        &nbsp;
        <div>
          <input
            name="Phone"
            className={`text ${errors.Phone && "input-error"}`}
            value={formData.Phone}
            onChange={handleChange}
            placeholder="전화번호 ex)010-1234-5678"
          ></input>
          {errors.Phone && <div className="error-message">{errors.Phone}</div>}
        </div>
        &nbsp;
        <div>
          <input
            name="Birth"
            type="date"
            className={`text ${errors.Birth && "input-error"}`}
            value={formData.Birth}
            onChange={handleChange}
            onKeyDown={(event) => {
              event.preventDefault();
            }}
            placeholder="생년월일 (YYYY/MM/DD)"
          />
          {errors.Birth && <div className="error-message">{errors.Birth}</div>}
        </div>
        &nbsp;
        <button onClick={handleSignUp} className="SignUp-button-SignUp">
          회원 가입
        </button>
      </div>
    </div>
  );
}

export default SignUp;
