export const handleChangeWithErrorCheck = (
  e,
  handleChange,
  updateErrors,
  formData
) => {
  const { name, value } = e.target;
  handleChange(e);
  updateErrors(name, value, formData);
};

export const handleSubmit = (
  e,
  formData,
  checkErrors,
  Sign_Up,
  setFormData,
  navigate
) => {
  e.preventDefault();
  const hasErrors = checkErrors(formData);
  if (!hasErrors) {
    Sign_Up(formData, setFormData, navigate);
  }
};
