import React from "react";
import "../Style/FindID.css";
import { useNavigate } from "react-router-dom";
import { useFormData } from "../Function/useFormData";
import { Find_ID } from "../Function/FindID_Find_ID";
import {
  useFormErrors,
  handleChangeWithErrorCheck,
  handleSubmit,
} from "../Function/useFormErrors";

function FindID() {
  const navigate = useNavigate();
  const [formData, handleChange, setFormData] = useFormData({
    Phone: "",
  });

  const { errors, updateErrors, checkErrors } = useFormErrors({
    Phone: false,
  });

  const handleInputChange = (e) => {
    handleChangeWithErrorCheck(e, handleChange, updateErrors, formData);
  };

  const handleFormSubmit = (e) => {
    handleSubmit(e, formData, checkErrors, Find_ID, setFormData, navigate);
  };

  return (
    <div className="FindID-section">
      <h2>아이디 찾기</h2>
      &nbsp;
      <input
        name="Phone"
        className="text"
        value={formData.Phone}
        onChange={handleChange}
        placeholder="전화번호"
      />
      <div className="text-box-ID">
        {errors.Phone && "전화번호 형식이 올바르지 않습니다. 예: 010-1234-5678"}
      </div>
      <button
        onClick={() => Find_ID(formData, setFormData, navigate)}
        className="FindID-button-FindID"
      >
        찾기
      </button>
    </div>
  );
}

export default FindID;
