import React from "react";
import "../Style/FindPW.css";
import { useNavigate } from "react-router-dom";
import { useFormData } from "../Function/useFormData";
import { Find_PW } from "../Function/FindPW_Find_PW";
import {
  useFormErrors_SignUP,
  handleChangeWithErrorCheck,
  handleSubmit_Find_PW,
} from "../Function/useFormErrors";

function FindPW() {
  const navigate = useNavigate();
  const [formData, handleChange, setFormData] = useFormData({
    ID: "",
    Phone: "",
  });
  const { errors, updateErrors, checkErrors } = useFormErrors_FindID({
    ID: false,
    Phone: false,
  });

  const handleInputChange = (e) => {
    handleChangeWithErrorCheck(e, handleChange, updateErrors, formData);
  };

  const handleFormSubmit = (e) => {
    handleSubmit_Find_PW(
      e,
      formData,
      checkErrors,
      Find_PW,
      setFormData,
      navigate
    );
  };

  return (
    <div className="FindPW-section">
      <h2>비밀번호 찾기</h2>
      <form onSubmit={handleFormSubmit} className="signup-form">
        <input
          name="ID"
          className="text"
          value={formData.ID}
          onChange={handleFormSubmit}
          placeholder="아이디"
        />
        &nbsp;
        <input
          name="Phone"
          className="text"
          value={formData.Phone}
          onChange={handleFormSubmit}
          placeholder="전화번호"
        />
        &nbsp;
        <button
          onClick={() => Find_PW(formData, setFormData, navigate)}
          className="FindPW-button-FindPW"
        >
          찾기
        </button>
      </form>
    </div>
  );
}

export default FindPW;
