import React from "react";
import "../Style/FindID.css";
import { useNavigate } from "react-router-dom";
import { useFormData } from "../Function/useFormData";
import { Find_ID } from "../Function/FindID_Find_ID";
import {
  useFormErrors,
  handleChangeWithErrorCheck,
  handleSubmit_Find_ID,
} from "../Function/useFormErrors";

function FindID() {
  const navigate = useNavigate();
  const [formData, handleChange, setFormData] = useFormData({
    Phone: "",
  });

  const { errors, updateErrors, checkErrors } = useFormErrors({
    Phone: false,
  });

  const handleInputChange = (e) => {
    handleChangeWithErrorCheck(e, handleChange, updateErrors, formData);
  };

  const handleFormSubmit = (e) => {
    handleSubmit_Find_ID(
      e,
      formData,
      checkErrors,
      Find_ID,
      setFormData,
      navigate
    );
  };

  return (
    <div className="FindID-section">
      <h2>아이디 찾기</h2>
      <form onSubmit={handleFormSubmit} className="find-id-form">
        <div className="form-group">
          <label htmlFor="Phone">전화번호</label>
          <input
            type="text"
            id="Phone"
            name="Phone"
            className={`text ${errors.Phone ? "error" : ""}`}
            value={formData.Phone}
            onChange={handleInputChange}
            placeholder="전화번호"
          />
          {errors.Phone && (
            <div className="error-message">
              전화번호 형식이 올바르지 않습니다. 예: 010-1234-5678
            </div>
          )}
        </div>
        <button type="submit" className="FindID-button-FindID">
          찾기
        </button>
      </form>
    </div>
  );
}

export default FindID;
