// src/utils/shuffleImages.js
import { shuffle } from "lodash";
import summerImage from "../images/summer.jpg";
import summerImage1 from "../images/summer1.jpg";
import summerImage2 from "../images/summer2.jpg";
import maplekingImage from "../images/mapleking.jpg";
import junsungImage from "../images/junsung.jpg"; // 예시로 추가한 이미지

const images = [
  summerImage,
  summerImage1,
  summerImage2,
  maplekingImage,
  junsungImage,
];

export const getRandomImages = (num = 4) => {
  const shuffledImages = shuffle(images);
  return shuffledImages.slice(0, num);
};
