import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./test_module.css";
import Menubar from "../Menubar";

const scokets = require("./sockets.js");

async function test(formData) {
  // SignUp을 그대로 가져와서 테스트
  function TestModule() {
    const navigate = useNavigate();

    const HandleClick = (url) => {
      navigate(url);
    };

    /*const [text, setText] = useState("");*/

    const [formData, setFormData] = useState({
      ID: "",
      PW: "",
      confirmPW: "",
      nickname: "",
      name: "",
      Phone: "",
      Birth: "",
    });

    const [showPassword, setShowPassword] = useState(false);

    const Sign_Up = () => {
      formData.ID = "a";
      formData.PW = "b";
      formData.nickname = "c";
      formData.name = "d";
      formData.Phone = "e";
      formData.Birth = "f";

      var save = scokets.UserData_Save(formData);
      alert(save.UserData_Save_Result);
    };
  }

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleKeyDown = (event) => {
    event.preventDefault();
  };

  const toggleShowPassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  return (
    <div className="App">
      <Menubar></Menubar>
      <div className="App-content-SignUp">
        <div>
          <input
            name="ID"
            className="text"
            value={formData.ID}
            onChange={handleChange}
            placeholder="아이디"
          ></input>
        </div>
        &nbsp;
        <div>
          <input
            type={showPassword ? "text" : "password"}
            name="PW"
            className="text password-input"
            value={formData.PW}
            onChange={handleChange}
            placeholder="비밀번호(8 ~ 32자)"
          ></input>
          <input
            type="checkbox"
            checked={showPassword}
            onChange={toggleShowPassword}
          ></input>
        </div>
        &nbsp;
        <div>
          <input
            type="password"
            name="confirmPW"
            className="text"
            value={formData.confirmPW}
            onChange={handleChange}
            placeholder="비밀번호 재입력"
          ></input>
        </div>
        &nbsp;
        <div>
          <input
            name="nickname"
            className="text"
            value={formData.nickname}
            onChange={handleChange}
            placeholder="별명"
          ></input>
        </div>
        &nbsp;
        <div>
          <input
            name="name"
            className="text"
            value={formData.name}
            onChange={handleChange}
            placeholder="이름"
          ></input>
        </div>
        &nbsp;
        <div>
          <input
            name="Phone"
            className="text"
            value={formData.Phone}
            onChange={handleChange}
            placeholder="전화번호"
          ></input>
        </div>
        &nbsp;
        <div>
          <input
            name="Birth"
            type="date"
            className="text"
            value={formData.Birth}
            onChange={handleChange}
            onKeyDown={handleKeyDown}
            placeholder="생년월일 (YYYY/MM/DD)"
          />
        </div>
        &nbsp;
        <button onClick={Sign_Up} className="SignUp-button-SignUp">
          회원 가입
        </button>
      </div>
    </div>
  );
}

export default TestModule;
