import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../Style/SignUp.css";
import Menubar from "./Menubar";

function SignUp() {
  const navigate = useNavigate();

  const HandleClick = (url) => {
    navigate(url);
  };

  /*const [text, setText] = useState("");*/

  const [formData, setFormData] = useState({
    ID: "",
    PW: "",
    confirmPW: "",
    nickname: "",
    name: "",
    Phone: "",
    Birth: "",
  });

  /*const handleChange = (event) => {
    setFormData(event.target.value);
  };*/

  const [passwordMismatch, setPasswordMismatch] = useState(false); // 상태 변수 추가
  const Sign_Up = () => {
    if (formData.PW !== formData.confirmPW) {
      alert("비밀번호가 일치하지 않습니다.");
      return;
    }
    alert(JSON.stringify(formData));
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
    if (name === "PW" || name === "confirmPW") {
      if (formData.PW !== formData.confirmPW) {
        setPasswordMismatch(true);
      } else {
        setPasswordMismatch(false);
      }
    }
  };

  return (
    <div className="App">
      <Menubar></Menubar>
      <div className="App-content-SignUp">
        <div>
          <textarea
            name="ID"
            className="text"
            value={formData.ID}
            onChange={handleChange}
            placeholder="아이디"
          ></textarea>
        </div>
        &nbsp;
        <div>
          <textarea
            name="PW"
            className="text"
            value={formData.PW}
            onChange={handleChange}
            placeholder="비밀번호(8 ~ 32자)"
          ></textarea>
        </div>
        &nbsp;
        <div>
          <input
            name="confirmPW"
            className="text"
            value={formData.confirmPW}
            onChange={handleChange}
            placeholder="비밀번호 재입력"
          ></input>
        </div>
        {passwordMismatch && (
          <p style={{ color: "red" }}>비밀번호가 일치하지 않습니다.</p>
        )}
        &nbsp;
        <div>
          <textarea
            name="nickname"
            className="text"
            value={formData.nickname}
            onChange={handleChange}
            placeholder="별명"
          ></textarea>
        </div>
        &nbsp;
        <div>
          <textarea
            name="name"
            className="text"
            value={formData.name}
            onChange={handleChange}
            placeholder="이름"
          ></textarea>
        </div>
        &nbsp;
        <div>
          <textarea
            name="Phone"
            className="text"
            value={formData.Phone}
            onChange={handleChange}
            placeholder="전화번호"
          ></textarea>
        </div>
        &nbsp;
        <div>
          <input
            name="Birth"
            type="date"
            className="text"
            value={formData.Birth}
            onChange={handleChange}
            placeholder="생년월일 (YYYY/MM/DD)"
          />
        </div>
        &nbsp;
        <button onClick={Sign_Up} className="SignUp-button-SignUp">
          회원 가입
        </button>
      </div>
    </div>
  );
}

export default SignUp;
